import React, { useState } from 'react';
function MCQ({ question, options, correctAnswer }) {
  const [selected, setSelected] = useState(null);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const handleSubmit = () => {
    setIsSubmitted(true);
  };
  return (
    <div className='bg-light-gray p-2 mb-2'>
      <h2 className='text-xl font-semibold'>{question}</h2>
      <div className='mcq-options flex flex-wrap gap-2 mt-2'>
        {options.map((opt, index) => (
          <button
            key={index}
            className={`mcq-option ${selected === index ? 'bg-blue-100' : 'bg-white'}`}
            onClick={() => setSelected(index)}
            disabled={isSubmitted}
          >
            {String.fromCharCode(97 + index)}. {opt}
          </button>
        ))}
      </div>
      {!isSubmitted && (
        <button className='nav-button mt-2 w-full' onClick={handleSubmit}>
          Submit
        </button>
      )}
      {isSubmitted && (
        <p className={selected === correctAnswer ? 'text-green-600' : 'text-red-600'}>
          {selected === correctAnswer ? 'Correct!' : 'Incorrect'}
        </p>
      )}
    </div>
  );
}
export default MCQ;
