// Save as ErrorHandler.jsx
// Create: /Users/adrian/Desktop/AI_Expert_Course_App/src/components/ErrorHandler.jsx
import React, { useState, useEffect } from 'react';
function ErrorHandler() {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch');
        return res.json();
      })
      .then(setData)
      .catch(err => setError(err.message));
  }, []);
  return (
    <div className='container'>
      {error && <p className='error-message'>{error}</p>}
      {data.map(item => <p key={item.id} className='p-2'>{item.name}</p>)}
    </div>
  );
}
export default ErrorHandler;
// In App.jsx: import ErrorHandler from './components/ErrorHandler'; <ErrorHandler />

