// Save as DynamicApp.jsx
// Create: /Users/adrian/Desktop/AI_Expert_Course_App/src/DynamicApp.jsx
// Run in browser via Vite
// Create /Users/adrian/Desktop/projects/sample.json: {"displayName": "Test", "lessons": []}
import React, { useState, useEffect } from 'react';
function DynamicApp() {
  const [files, setFiles] = useState([]);
  const [dataMap, setDataMap] = useState({});
  useEffect(() => {
    const loadFiles = async () => {
      const importedFiles = import.meta.glob('./data/*.json', { eager: true });
      const loadedFiles = [];
      const loadedData = {};
      for (const [path, module] of Object.entries(importedFiles)) {
        const fileName = path.split('/').pop();
        const data = module.default;
        if (data.displayName && data.lessons) {
          loadedFiles.push(fileName);
          loadedData[fileName] = data;
        }
      }
      setFiles(loadedFiles);
      setDataMap(loadedData);
    };
    loadFiles();
  }, []);
  return (
    <div className='container'>
      <select className='training-selector'>
        {files.map(file => (
          <option key={file} value={file}>{dataMap[file]?.displayName || file}</option>
        ))}
      </select>
    </div>
  );
}
export default DynamicApp;
// In App.jsx: import DynamicApp from './DynamicApp'; <DynamicApp />

