import React, { useState, useEffect } from 'react';
function LessonLoader() {
  const [dataMap, setDataMap] = useState({});
  useEffect(() => {
    const files = import.meta.glob('./data/*.json', { eager: true });
    const loadedData = {};
    for (const [path, module] of Object.entries(files)) {
      loadedData[path.split('/').pop()] = module.default;
    }
    setDataMap(loadedData);
  }, []);
  return (
    <div className='container'>
      {Object.values(dataMap).map((data, index) => (
        <p key={index} className='lesson-item'>{data.displayName}</p>
      ))}
    </div>
  );
}
export default LessonLoader;
// In App.jsx: import LessonLoader from './components/LessonLoader'; <LessonLoader />

