// Update src/App.jsx to fix race condition
// Problem: Switching trainingFile (e.g., to lessons.json) saves currentLesson (e.g., 6) before loading saved lesson (e.g., 0), syncing all files to lesson 6
// Save as App.jsx
// Path: /Users/adrian/Desktop/AI_Expert_Course_App/src/App.jsx
import React, { useState, useEffect } from 'react';
function App() {
  const [currentLesson, setCurrentLesson] = useState(() => {
    const savedLessons = localStorage.getItem('lessonHistory');
    const savedFile = localStorage.getItem('trainingFile') || 'lessons.json';
    const lessonMap = savedLessons ? JSON.parse(savedLessons) : {};
    return lessonMap[savedFile] && !isNaN(parseInt(lessonMap[savedFile], 10)) ? parseInt(lessonMap[savedFile], 10) : 0;
  });
  const [trainingFile, setTrainingFile] = useState(localStorage.getItem('trainingFile') || 'lessons.json');
  useEffect(() => {
    localStorage.setItem('trainingFile', trainingFile);
  }, [trainingFile]);
  useEffect(() => {
    const lessonMap = JSON.parse(localStorage.getItem('lessonHistory') || '{}');
    lessonMap[trainingFile] = currentLesson;
    localStorage.setItem('lessonHistory', JSON.stringify(lessonMap));
  }, [currentLesson]);
  useEffect(() => {
    const lessonMap = JSON.parse(localStorage.getItem('lessonHistory') || '{}');
    const savedLesson = lessonMap[trainingFile];
    const parsedLesson = parseInt(savedLesson, 10);
    setCurrentLesson(savedLesson && !isNaN(parsedLesson) ? parsedLesson : 0);
  }, [trainingFile]);
  return (
    <div className='container'>
      <select value={trainingFile} onChange={(e) => setTrainingFile(e.target.value)}>
        <option value='lessons.json'>Default Course</option>
      </select>
      <select value={currentLesson} onChange={(e) => setCurrentLesson(Number(e.target.value))}>
        <option value={0}>Lesson 1</option>
      </select>
      <p>Selected: {trainingFile}, Lesson {currentLesson + 1}</p>
    </div>
  );
}
export default App;
// localStorage after switching to lessons.json: {'trainingFile': 'lessons.json', 'lessonHistory': {'aiProgramming.json': 6, 'lessons.json': 0}}
