// Data Mesh Principles Overview
// No code execution; conceptual diagram
Domains: { Marketing, Sales, HR }
Data Products: {
  Marketing: { data: 'customer_profiles', pipeline: 'ADF_pipeline', interface: 'API' },
  Sales: { data: 'orders', pipeline: 'Synapse_pipeline', interface: 'SQL_view' }
}
Governance: Microsoft Purview
Self-Serve Platform: Azure Data Lake Storage, Synapse Analytics
Federated Governance: { policies: ['access_control', 'encryption'], team: ['domain_experts', 'data_engineers'] }

// Federated Governance Example
// No code execution; conceptual
Governance Layer: {
  Tool: 'Microsoft Purview',
  Policies: ['data_access', 'encryption', 'lineage'],
  Team: ['domain_experts', 'compliance_officers']
}
Domains: {
  Sales: { data_product: 'orders', owner: 'sales_team' },
  Marketing: { data_product: 'campaigns', owner: 'marketing_team' }
}
Self-Serve Platform: Azure Synapse Analytics

// Run in Azure CLI
// Save as setup_adls.sh
// Run: bash /Users/adrian/Desktop/AI_Expert_Course_App/src/setup_adls.sh
az storage account create --name mydatalake --resource-group mygroup --location eastus --sku Standard_LRS --kind StorageV2 --hns true
az storage fs create -n sales --account-name mydatalake
az storage fs create -n marketing --account-name mydatalake
az storage fs access set --path sales/ --permissions rwxr-x--- --group sales_team --account-name mydatalake
az storage fs access set --path marketing/ --permissions rwxr-x--- --group marketing_team --account-name mydatalake
echo 'ADLS Gen2 configured with domain folders: sales/, marketing/'

// Synapse Pipeline Example
// Run in Azure Synapse Studio
// Save pipeline as sales_pipeline.json
{
  "name": "SalesPipeline",
  "activities": [
    {
      "name": "CopySalesData",
      "type": "Copy",
      "inputs": [{ "referenceName": "SalesADLS", "type": "DatasetReference" }],
      "outputs": [{ "referenceName": "SalesSQLPool", "type": "DatasetReference" }]
    }
  ]
}
// SQL View
CREATE VIEW sales.orders_view AS
SELECT order_id, customer_id, amount FROM sales_table;
// Publish to Purview
CALL system.purview.register_data_product('sales.orders_view', 'Sales Domain');

// Purview Governance Setup
// Run in Azure CLI
// Save as setup_purview.sh
// Run: bash /Users/adrian/Desktop/AI_Expert_Course_App/src/setup_purview.sh
az purview account create --name mypurview --resource-group mygroup --location eastus
az purview collection create --account-name mypurview --collection-name Sales
az purview policy create --account-name mypurview --policy-name sales_access --rules '[{"effect": "allow", "principal": "marketing_team", "actions": ["read"]}]'
// Register Data Product
CALL system.purview.register('sales.orders_view', 'Sales Domain', 'ADLS://mydatalake/sales/orders.parquet');
echo 'Purview configured with Sales collection and access policy.'

// Self-Serve Platform Setup
// Run in Azure CLI
// Save as setup_self_serve.sh
// Run: bash /Users/adrian/Desktop/AI_Expert_Course_App/src/setup_self_serve.sh
az synapse workspace create --name mysynapse --resource-group mygroup --storage-account mydatalake --location eastus
az role assignment create --role 'Contributor' --assignee sales_team --scope /subscriptions/my-sub/resourceGroups/mygroup/providers/Microsoft.Synapse/workspaces/mysynapse
az function create --name SalesAPI --resource-group mygroup --location eastus --runtime python --functions-version 4
// Deploy API (simplified)
echo 'def main(req): return func.HttpResponse(json.dumps({"data": "sales_orders"}))' > SalesAPI/function.py
echo 'Self-serve platform: Synapse workspace and Sales API deployed.'

// Save as sales_pipeline.py
// Run: python /Users/adrian/Desktop/AI_Expert_Course_App/src/sales_pipeline.py
// Requires: pip install azure-synapse-artifacts azure-identity
from azure.synapse.artifacts import ArtifactsClient
from azure.identity import DefaultAzureCredential
from azure.synapse.artifacts.models import *
credential = DefaultAzureCredential()
client = ArtifactsClient(credential, 'https://mysynapse.dev.azuresynapse.net')
pipeline = PipelineResource(
  activities=[
    CopyActivity(
      name='CopySalesData',
      inputs=[DatasetReference(reference_name='SalesADLS')],
      outputs=[DatasetReference(reference_name='SalesSQLPool')]
    )
  ]
)
client.pipeline.create_or_update_pipeline('SalesPipeline', pipeline)
print('Pipeline created: SalesPipeline')

// Cross-Domain Query
// Run in Synapse SQL Pool
// Save as customer_360.sql
CREATE VIEW customer_360 AS
SELECT s.order_id, s.amount, m.customer_id, m.campaign_clicks
FROM sales.orders_view s
JOIN marketing.customers_view m
ON s.customer_id = m.customer_id;
// Register in Purview
CALL system.purview.register('customer_360', 'Cross-Domain', 'Synapse://mysynapse/customer_360');
-- Query
SELECT * FROM customer_360 LIMIT 5;

// Landing Zone Setup
// Run in Azure CLI
// Save as setup_landing_zones.sh
// Run: bash /Users/adrian/Desktop/AI_Expert_Course_App/src/setup_landing_zones.sh
az group create --name sales-landing-zone --location eastus
az group create --name marketing-landing-zone --location eastus
az network vnet create --name sales-vnet --resource-group sales-landing-zone --address-prefix 10.1.0.0/16
az network vnet create --name marketing-vnet --resource-group marketing-landing-zone --address-prefix 10.2.0.0/16
az network vnet peering create --name SalesToMgmt --vnet-name sales-vnet --remote-vnet /subscriptions/my-sub/resourceGroups/mgmt-group/providers/Microsoft.Network/virtualNetworks/mgmt-vnet --allow-vnet-access
echo 'Landing zones created with VNet peering.'

// Monitoring Setup
// Run in Azure CLI
// Save as setup_monitoring.sh
// Run: bash /Users/adrian/Desktop/AI_Expert_Course_App/src/setup_monitoring.sh
az monitor diagnostic-settings create --name synapse-monitoring --resource /subscriptions/my-sub/resourceGroups/mygroup/providers/Microsoft.Synapse/workspaces/mysynapse --logs '[{"category": "PipelineRuns", "enabled": true}]' --metrics '[{"category": "AllMetrics", "enabled": true}]' --workspace /subscriptions/my-sub/resourceGroups/mygroup/providers/Microsoft.OperationalInsights/workspaces/myloganalytics
echo 'Monitoring enabled for Synapse pipelines.'
// In Purview: View lineage for 'sales.orders_view'
// UI: Search 'sales.orders_view' -> Lineage tab

// Financial Data Mesh
// No code execution; conceptual
Data Management Zone: {
  Governance: 'Microsoft Purview',
  Subscription: 'mgmt-sub',
  Services: ['ADLS', 'Synapse', 'Monitor']
}
Domain Zones: [
  { name: 'Transactions', subscription: 'trans-sub', data_product: '/bank/transactions.parquet' },
  { name: 'Customers', subscription: 'cust-sub', data_product: '/bank/customers.parquet' }
]
VNet Peering: trans-sub -> mgmt-sub, cust-sub -> mgmt-sub
// Query
SELECT t.transaction_id, c.customer_name
FROM transactions.transactions_view t
JOIN customers.customers_view c
ON t.customer_id = c.customer_id;

